import React from 'react';
import logo from '../logo.svg';

class Body extends React.Component{

    constructor(props){
        super(props);

        this.user = this.props.userDetails;
        this.add = this.props.userDetails.address;

        this.state = {
            firstName : this.user.firstName,
            lastName : this.user.lastName,
            email : this.user.email,
            city : this.add.city,
            location : this.user.location,
            pincode : this.add.pincode
        }


         // This binding is necessary to make `this` work in the callback
        this.handleEvent = this.handleEvent.bind(this);
        this.inputUpdate = this.inputUpdate.bind(this);
    }

    // we can't change set but we can re-initiate and reassign it.
    handleEvent(){
        this.setState(state => ({
            firstName : "Santosh",
            lastName : "jala",
            email : "santosh.jala@gmail.com",
            city : "Hyderabad",
            location : "kukatpally",
            pincode : "500072",
            inputVal :  ""
        }));
    }

    
    inputUpdate = e => {
        console.log(e);
        
        this.setState({ 
           'inputVal' : e.target.value 
        })
    };
    
    render(){
        
        return(
            <div>
                <img src={logo} className="App-logo" alt="logo" /> <br/>

                <input type="text" onChange={this.inputUpdate}></input> 
                <br></br>
                <p> 
                    <h1>
                        {this.state.inputVal}
                    </h1>
                </p>
                
                <br></br>
                <button onClick={this.handleEvent}>Change Name</button>
                <ul>
                    <li> <b> First Name: </b> {this.state.firstName}  </li>
                    <li> <b> Last Name: </b> {this.state.lastName}  </li>
                    <li> <b> Location: </b> {this.state.location}  </li>
                    <li> <b> email: </b> {this.state.email}  </li>

                    <li> <b> City: </b> {this.state.city}  </li>
                    <li> <b> Pincode: </b> {this.state.pincode}  </li>
            
                </ul>
                <button className="btn btn-primary">On</button>
            </div>
        );
    }
}


export default Body;
